import React from 'react';


export default function Hero() {

  return (
    <div className="float top-0 bg-white overflow-hidden mb-auto z-0 items-center justify-center"
      style={
        {
          background: 'url(https://www.mcdonalds.com.my/storage/carousel/7f366266340f55db81dc7d53357eee7d.jpg',
          width: '100%',
          height: 'calc(100vh - 64px)',
        }
      }
    >
      <div className="max-w-7xl mx-auto">

        <div className="relative z-10 pb-8 sm:pb-16 md:pb-20 lg:max-w-2xl lg:w-full lg:pb-28 xl:pb-32">
          <main className="mt-10 mx-auto max-w-7xl px-4 sm:mt-12 sm:px-6 md:mt-16 lg:mt-20 lg:px-8 xl:mt-28">
            <div className="sm:text-center lg:text-left">
              <h1 className="bg-transparent  text-4xl tracking-tight font-bold text-gray-900 sm:text-5xl sm:tracking-tight md:text-6xl md:tracking-tight" style={{
                textAlign: "center",
                marginTop: '40%',
                width: '100',
              }}>
                <span className="block xl:inline col-md-6 col-md-offset-3">Welcome</span>{' '}
                <span className="block text-indigo-600 xl:inline">Utarian</span>
              </h1>
              <p className=" text-center mt-3 text-base text-gray-500 sm:mt-5 sm:text-lg sm:max-w-xl sm:mx-auto md:mt-5 md:text-xl lg:mx-0">
                Burpy reimagines the wonders of Universal cuisine fare in a space that evokes a bygone era of a boisterous yet elegant cuisines within the heart of yours.
              </p>
            </div>
          </main>
        </div>
      </div>

      <div className="lg:absolute  lg:inset-y-20 lg:right-0 lg:w-1/2 lg:h-1/2">
        <img
          className="hidden w-full object-cover sm:hidden md:hidden lg:block lg:w-full lg:h-full lg:object-contain"
          src="https://www.nicepng.com/png/full/648-6481084_clip-art-royalty-free-library-burger-at-getdrawings.png"
          alt=""
        />
      </div>
    </div >
  )
};
