import { Link } from "react-router-dom";

import background from "../assets/bgr1.jpg";
import btn from "../assets/btn1.png";



import { Swiper, SwiperSlide } from "swiper/react";
import { Mousewheel, Pagination, Scrollbar } from "swiper";
import 'swiper/css/bundle';



export default function App() {

  const slides = [
    { id: 1, Element: <h1>Slide 1</h1> },
    { id: 2, Element: <h1>Slide 2</h1> },
    { id: 3, Element: <h1>Slide 3</h1> }
  ]

  const slide_style = 
    {
      display: "flex",
      textAlign: "center",
      alignItems: "center",
      justifyContent: "center",
      height: "100%"
    }

  return (
    <>
      <Swiper className="mySwiper" direction={"horizontal"} slidesPerView={1}
      style={
        {
          width: "100%",
          height: 'calc(100vh - 64px)'
        }
      }
      modules={[Mousewheel, Pagination]}
      effect={
        {
          slideShadows: true,
          fadeEffect: {
            crossFade: true
          }
        }
      }
      autoHeight={"true"}
      mousewheel={{ forceToAxis: true, sensitivity: 1, releaseOnEdges: true }}
      pagination={{
        clickable: true,
      }
      }
    >
      {slides.map(slide => 
      <SwiperSlide key={slide.id} 
      style={slide_style}> 
      {slide.Element} 
      </SwiperSlide>)}
    </Swiper>


      {/* social media plugins */}
      <div style={{
        position: 'fixed',
        top: '90%',
        left: '5%',
        transform: 'translate(-50%, -50%)',
        color: 'black',
        fontSize: '50px',
        zIndex: '2',
        opacity: '1.5',
      }}>
        <div className="box">
          {/* facebook share */}
          <FacebookShareButton
            url={"https://www.facebook.com/React-Bootstrap-109811025727740"}
            quote={"React-Bootstrap"}
            hashtag="#reactbootstrap"
          >
            <FacebookIcon size={32} round />
          </FacebookShareButton>
          {/* twitter share */}
          <TwitterShareButton
            url={"https://www.facebook.com/React-Bootstrap-109811025727740"}
            quote={"React-Bootstrap"}
            hashtag="#reactbootstrap"
          >
            <TwitterIcon size={32} round />
          </TwitterShareButton>

        </div>

      </div>

    </>
  );
}
