// Home.js

import React, { Component } from 'react';
import Hero from '../components/Hero';

import { Swiper, SwiperSlide } from "swiper/react";
import { FreeMode, Mousewheel } from "swiper";
import 'swiper/css/bundle';
import Ads from '../components/Ads';
import MenuIntro from '../components/Menu-orderIntro';

import {
  FacebookShareButton,
  TwitterShareButton,
  FacebookIcon,
  TwitterIcon,


} from "react-share";

export default class Home extends Component {

  swiper_style = {
    width: "100%",
    height: 'calc(100vh - 64px)'
  }

  slide_style = {
    display: "flex",
    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
  }

  slides = [
    { id: 1, Element: <Hero /> },
    { id: 2, Element: <Ads/> },
    { id: 3, Element: <MenuIntro/> }
  ]

  render() {
    return (
      <div>
      <Swiper className="mySwiper" direction={"vertical"} slidesPerView={1} style={this.swiper_style}
        modules={[FreeMode, Mousewheel]}
        direction
        effect={"coverflow"}
        autoHeight={"true"}
        freeMode={"true"}
        mousewheel={{
          forceToAxis: true,
          sensitivity: 0.5,
          releaseOnEdges: true,
        }}

      >

        {this.slides.map(slide => <SwiperSlide key={slide.id} style={this.slide_style}> {slide.Element} </SwiperSlide>)}

   </Swiper>

   <div style={{
        position: 'fixed',
        top: '90%',
        left: '5%',
        transform: 'translate(-50%, -50%)',
        zIndex: '2',


      }}>
        <div className="box">
          {/* facebook share */}
          <FacebookShareButton
            url={"https://www.facebook.com/React-Bootstrap-109811025727740"}
            quote={"React-Bootstrap"}
            hashtag="#reactbootstrap"
          >
            <FacebookIcon size={32} round />
          </FacebookShareButton>
          {/* twitter share */}
          <TwitterShareButton
            url={"https://www.facebook.com/React-Bootstrap-109811025727740"}
            quote={"React-Bootstrap"}
            hashtag="#reactbootstrap"
          >
            <TwitterIcon size={32} round />
          </TwitterShareButton>

        </div>

      </div>
</div>



    );
  }
}
