import React from 'react';
import { Link } from 'react-router-dom';
import btn from '../assets/btn1.png';
import background from '../assets/bgr1.jpg';

export default function MenuIntro() {


  return (
    <div className="float top-0 bg-white overflow-hidden mb-auto z-0 items-center justify-center">
      <img src={background} alt="Unknown" style={
        {
          position: 'absolute',
// disply center
          top: '50%',

          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '100%',
          height: '100%',

        }
      }/>


      <Link to='/menu' style={{
        backgroundImage: `url(${btn})`,
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        height: '105px',
        width: '250px',
        position: 'relative',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, 10%)',
        zIndex: '2',
        opacity: '1.5',
        backgroundSize: 'cover',

      }} >
      </Link>

      <h1 style={{
        color: 'black',
        fontSize: '50px',
        position: 'relative',
        top: '46%',
        left: '41.5%',

      }}> <b>Our Food</b></h1>
    </div>
  )
}
