// Home.js

import React, { Component } from 'react';
import Hero from '../components/Hero';

import { Swiper, SwiperSlide } from "swiper/react";
import { Mousewheel, Scrollbar } from "swiper";
import 'swiper/css/bundle';
import Ads from '../components/Ads';
import MenuIntro from '../components/MenuIntro';

import {
  FacebookShareButton,
  TwitterShareButton,
  FacebookIcon,
  TwitterIcon,


} from "react-share";

export default class Home extends Component {

  swiper_style = {
    width: "100%",
    height: 'calc(100vh - 64px)'
  }

  slide_style = {
    display: "flex",
    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
  }

  slides = [
    { id: 1, Element: <Hero /> },
    { id: 2, Element: <Ads/> },
    { id: 3, Element: <MenuIntro/> }
  ]

  render() {
    return (
      <div>
      <Swiper className="mySwiper" direction={"vertical"} slidesPerView={1} style={this.swiper_style}
        modules={[Mousewheel, Scrollbar]}
        effect={"coverflow"}
        autoHeight={"true"}
        mousewheel={{
          forceToAxis: true,
          sensitivity: 1,
          releaseOnEdges: true,
        }}
        scrollbar={{ hide: true, draggable: true, snapOnRelease: false }}
        // Auto height

      >

        {this.slides.map(slide => <SwiperSlide key={slide.id} style={this.slide_style}> {slide.Element} </SwiperSlide>)}

   </Swiper>

   <div></div>
</div>



    );
  }
}
