import { Link } from "react-router-dom";

import background from "../assets/bgr1.jpg";
import btn from "../assets/btn1.png";



import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay, Pagination, Scrollbar } from "swiper";
import 'swiper/css/bundle';



export default function App() {

  const slides = [
    { id: 1, Element: <img style={{width: "100%", height: "100%"}} src="https://www.mcdonalds.com.my/storage/carousel/4ea0613fc0f7b8c9bb8eafde1bab4fcf.png"/> },
    { id: 2, Element: <img style={{width: "100%"}} src="https://www.mcdonalds.com.my/storage/carousel/eff16b368c8d4cd4c663ed7341169046.png"/> },
    { id: 3, Element: <img style={{width: "100%"}} src="https://www.mcdonalds.com.my/storage/carousel/897fc31cdc1cde5eb9cf1cf70cdc2561.png"/> },
    { id: 4, Element: <img style={{width: "100%"}} src="https://www.mcdonalds.com.my/storage/carousel/10f630e74203faf117b29a3b90eb8ac1.png"/> },
  ]

  const slide_style = 
    {
      display: "flex",
      textAlign: "center",
      alignItems: "center",
      justifyContent: "center",
      margin: "0 auto",
      padding: "0 auto",
      height: "100%",
      width: "100%"
    }

  return (
      <Swiper className="mySwiper" direction={"horizontal"} slidesPerView={1}
      style={
        {
          width: "100%",
          height: 'calc(100vh - 64px)'
        }
      }
      autoHeight={true}
      autoplay={{delay: 2000}}
      spaceBetween={0}
      loop={true}
      modules={[Pagination, Autoplay]}
      pagination={{
        clickable: true,
      }

      }
    >
      {slides.map(slide => <SwiperSlide key={slide.id} style={slide_style}> {slide.Element} </SwiperSlide>)}
    </Swiper>
  );
}
